<?php // phpcs:ignore/** * Helper product class *//** * This is the class just for testing purpose * * @package Krokedil/tests */class Krokedil_Simple_Product extends AKrokedil_WC_Product {	/**	 * Indicate whether to save or not.	 *	 * @return bool	 */	public function save(): bool {		return true;	}	/**	 * Creates a simple product	 *	 * @return WC_Product_Simple product.	 */	public function create(): WC_Product {		$wc_product = new WC_Product_Simple();		$wc_product->set_props( $this->data );		if ( $this->save() ) {			$wc_product->save();			return wc_get_product( $wc_product );		}		return $wc_product;	}}