<?php // phpcs:ignore/** * Helper product class *//** * This is the class just for testing purpose * * @package Krokedil/tests */class Krokedil_Variation_Product extends AKrokedil_WC_Product {	/**	 * Products attributes	 *	 * @var array $attributes	 */	protected $attributes;	/**	 * Krokedil_Variation_Product constructor.	 *	 * @param array $data props.	 * @param array $attributes attributes.	 */	public function __construct( array $data = [], array $attributes = [] ) {		parent::__construct( $data );		if ( empty( $attributes ) ) {			$this->attributes = [ 'pa_size' => 'small' ];		} else {			$this->attributes = $attributes;		}	}	/**	 * Indicate whether to save or not.	 *	 * @return bool	 */	public function save(): bool {		return true;	}	/**	 * Creates a grouped product	 *	 * @return WC_Product_Variation product.	 */	public function create(): WC_Product {		$wc_product = new WC_Product_Variation();		$wc_product->set_props( $this->get_data() );		$wc_product->set_attributes( $this->attributes );		if ( $this->save() ) {			$wc_product->save();			return wc_get_product( $wc_product );		}		return $wc_product;	}}